//#include "core.h"
#include "emulator/MPIntWrapper.h"

void tests() {
    MPInt num1 = MPInt(
            "");
    MPInt num2 = MPInt(
            "");
    auto num3 = num1 * num2;
    assert(num3 ==
           "");
    num3 -= MPInt(1);
    assert(num3 ==
           "");
    num3 -= MPInt(-1);
    assert(num3 ==
           "");

    assert(num1 / num2 == "1");
    num2 = MPInt(
            "");
    assert(num1 / num2 == "10");

    num2 = MPInt(
            "");

    assert(num1 - num2 == "0");
    assert(num1 + num2 ==
           "");
    num2 = -num2;
    assert(num1 * num2 ==
           "-");
    assert(num1 - num2 ==
           "");
    assert(num1 + num2 == "0");
    num1 = 4;
    num2 = 2;
    assert(num1 / num2 == "2");
    num2 = -num2;
    assert(num1 / num2 == "-2");
}

void relationalOperatorsTest() {
    auto a = MPInt("2147483647");
    auto b = MPInt("2147483648");

    assert(a < b);
}

int main() {
    tests();
    relationalOperatorsTest();
    ///////////////////////////////
    auto a2 = MPIntWrapper<4>();
    a2.workWithTemplate();
    std::cout << a2 << std::endl;
    auto a = MPIntWrapper<4>("2147483647");
    std::cout << "OK:" << a << std::endl;
    std::cout << "ERROR?:" << a + 2 << std::endl;
    return 0;
}
