#include <iostream>
#include "MPInt.h"
#include "core.h"

void tests() {
    MPInt num2 = MPInt(
            "");
    MPInt num1 = MPInt(
            "");
    assert (num1 * num2 ==
            "");

    assert (num1 / num2 == "1");
    num2 = MPInt(
            "");
    assert (num1 / num2 == "10");

    num2 = MPInt(
            "");

    assert (num1 - num2 == "0");
    assert (num1 + num2 ==
            "");
    num2 = -num2;
    assert (num1 * num2 ==
            "-");
    assert (num1 - num2 ==
            "");
    assert (num1 + num2 == "0");
    num1 = 4;
    num2 = 2;
    assert(num1 / num2 == "2");
    num2 = -num2;
    assert(num1 / num2 == "-2");
}

int main() {
    tests();
    return 0;
}
