#include "MPInt.h"
#include "core.h"


void tests() {
    MPInt num1 = MPInt<4>(
            "");
    MPInt num2 = MPInt<6>(
            "");
    auto num3 = num1 * num2;
    assert (num3 ==
            "");
    num3 -= MPInt<10>(1);
    assert (num3 ==
            "");
    num3 -= MPInt<16>(-1);
    assert (num3 ==
            "");

    assert (num1 / num2 == "1");
    num2 = MPInt<4>(
            "");
    assert (num1 / num2 == "10");

    num2 = MPInt<45>(
            "");

    assert (num1 - num2 == "0");
    assert (num1 + num2 ==
            "");
    num2 = -num2;
    assert (num1 * num2 ==
            "-");
    assert (num1 - num2 ==
            "");
    assert (num1 + num2 == "0");
    num1 = 4;
    num2 = 2;
    assert(num1 / num2 == "2");
    num2 = -num2;
    assert(num1 / num2 == "-2");
    num1.workWithTemplate();
}

int main() {
    tests();
    return 0;
}
