//#include "core.h"
#include "emulator/MPInt.h"

void tests() {
    MPIntBase num1 = MPIntBase(
            "");
    MPIntBase num2 = MPIntBase(
            "");
    auto num3 = num1 * num2;
    assert(num3 ==
           "");
    num3 -= MPIntBase(1);
    assert(num3 ==
           "");
    num3 -= MPIntBase(-1);
    assert(num3 ==
           "");

    assert(num1 / num2 == "1");
    num2 = MPIntBase(
            "");
    assert(num1 / num2 == "10");

    num2 = MPIntBase(
            "");

    assert(num1 - num2 == "0");
    assert(num1 + num2 ==
           "");
    num2 = -num2;
    assert(num1 * num2 ==
           "-");
    assert(num1 - num2 ==
           "");
    assert(num1 + num2 == "0");
    num1 = 4;
    num2 = 2;
    assert(num1 / num2 == "2");
    num2 = -num2;
    assert(num1 / num2 == "-2");
}

void relationalOperatorsTest() {
    auto a = MPIntBase("2147483647");
    auto b = MPIntBase("2147483648");

    assert(a < b);
}

void streamTests() {
    std::stringstream ss{"2147483647"};
    MPInt<4> a{};
    ss >> a;
    assert(a == "2147483647");
}

int main() {
    tests();
    relationalOperatorsTest();
    streamTests();
    ///////////////////////////////
    auto a2 = MPInt<4>();
    std::cout << a2 << std::endl;
    auto a = MPInt<4>("2147483646");
    a += -2;
    std::cout << "OK:" << a << std::endl;
    return 0;
}
