#include "emulator/MPInt.h"

void tests() {
    MPIntBase num1 = MPIntBase(
            "");
    MPIntBase num2 = MPIntBase(
            "");
    auto num3 = num1 * num2;
    assert(num3 ==
           "");
    num3 -= MPIntBase(1);
    assert(num3 ==
           "");
    num3 -= MPIntBase(-1);
    assert(num3 ==
           "");

    assert(num1 / num2 == "1");
    num2 = MPIntBase(
            "");
    assert(num1 / num2 == "10");

    num2 = MPIntBase(
            "");

    assert(num1 - num2 == "0");
    assert(num1 + num2 ==
           "");
    num2 = -num2;
    assert(num1 * num2 ==
           "-");
    assert(num1 - num2 ==
           "");
    assert(num1 + num2 == "0");
    num1 = 4;
    num2 = 2;
    assert(num1 / num2 == "2");
    num2 = -num2;
    assert(num1 / num2 == "-2");
}

void relationalOperatorsTest() {
    auto a = MPIntBase("2147483647");
    auto b = MPIntBase("2147483648");

    assert(a < b);
}

void streamTests() {
    std::stringstream ss{std::to_string(INT_MAX)};
    MPInt<4> a{};
    ss >> a;
    assert(a == INT_MAX);
}

void factorialTest() {
    MPInt<UNLIMITED> a(24);
    a = factorial(a);
    try {
        MPInt<4> b = a;
    } catch (OverflowException &e) {
        std::cerr << "overflow err: " << e.number << std::endl;
    }
}

void assigmentTests() {
    MPInt<5> a = INT_MAX;
    MPInt<4> b = std::string("2");
    b = a;
    assert(b == a);
}


